import sys
from collections import deque
from dataclasses import dataclass
from typing import List


def sym_hash(sym: str):
    return ord(sym) - 65

class Solution:


    def minWindow(self, s: str, t: str) -> str:
        # idea: we will consider each character that exists in t
        # as part of potential window

        key_proto = [0 for _ in range(58)]

        for sym in t:
            key_proto[sym_hash(sym)] += 1

        key_symb = set(t)

        buffer_char = deque()
        buffer_idx = deque()
        key_length = len(t)

        idx = 0

        min_length = sys.maxsize
        st = None
        end = None

        while idx < len(s):
            sym = s[idx]
            if sym not in key_symb:
                idx += 1
                continue

            # update buffer contents
            buffer_char.append(sym)
            buffer_idx.append(idx)

            # update buffer len
            if key_length > 0 and key_proto[sym_hash(sym)] > 0:
                key_length -= 1

            key_proto[sym_hash(sym)] -= 1

            # cutting redundant head characteres
            while buffer_char and key_proto[sym_hash(buffer_char[0])] < 0:
                key_proto[sym_hash(buffer_char[0])] += 1
                buffer_char.popleft()
                buffer_idx.popleft()

            # update min bounds
            if key_length == 0:
                new_min_length = buffer_idx[-1] - buffer_idx[0] + 1
                if new_min_length < min_length:
                    st = buffer_idx[0]
                    end = buffer_idx[-1] + 1
                    min_length = new_min_length

            idx += 1

        return s[st:end] if st is not None else ""


def test_case1():
    s = "ADOBECODEBANC"
    t = "ABC"

    exp = "BANC"

    assert Solution().minWindow(s, t) == exp


